element.Formula:
try:
    db=context.getParentDatabase()
    dbscript=context.getParentDatabase().resources.application.script
    trList=dbscript.storiaWF(context)
    list = []
    for tr in trList:
        try:
            action=dbscript.getTitoloTransizione(tr['action'])
            list.append('"%s" eseguita il %s alle %s da %s' %(action,DateToString(tr['time'],'%d/%m/%Y'),DateToString(tr['time'],'%H:%M'),userFullname(db,tr['actor'])))
        except:
            pass
except:
    list=[]
return list

